	})
	containerBottom = Geyser.Container:new({
		name = "containerBottom", x = borderLeft, y = -borderBottom,
		width = "100%", height=borderBottom
	})

  -- UserWindow do wszystkich elementow dodatkowych do przesuwania po ekranie
