function createMinimap(parent)
	minimap = Geyser.MiniConsole:new({
		name="minimap", x = "-6c", y = "-4c",
		width = "5c", height = "4c",
		fontSize = 10
	}, parent)
	--minimap:setTextFormat(10, 50, 10, 255, 255, 255, 1, 0, 0);
	return minimap
end

