function jakiPoziomOpisu(tabelka, opis) for i, k in ipairs(tabelka) do if(type(k) == "table") then local idx, ss = jakiPoziomOpisu(k, opis) if(idx ~= nil) then return idx, ss end elseif(rex_pcre.new("^" .. k .. "$"):exec(opis)) then return i, #tabelka end end return nil; end
function jakaPozycjaOpisu(tabelka, opis) local mb, me, mk = 0, 0, nil for i, k in ipairs(tabelka) do if(type(k) == "table") then local b, e, k = jakaPozycjaOpisu(k, opis) if(b ~= nil and (me - mb < e - b)) then mb, me, mk = b, e, k end else local b, e = rex_pcre.new(k):exec(opis) if(b ~= nil and (me - mb < e - b)) then mb, me, mk = b, e, k end end end return mb, me, mk; end

--Pomoc
scriptsVersion = "3.8.0"
cecho("\n<white>Wpisz /help aby uzyskac pomoc.\n\n")

function makeHelpLink(tekst, description)
	selectString(tekst, 1);
	fg("white");
	setUnderline(true);
	setLink([[send("?]] .. tekst .. [[")]], description);
	resetFormat();
	deselect()
end

function makeHelpLinksFromSeeAlso(linia)
	local tematy = linia:gsub(" ", ""):gsub("\t", ""):split(",")
	for i,v in pairs(tematy) do
		makeHelpLink(v, v);
	end
end

function handleHelpCommands(eventName, cmd)
	if(cmd:sub(1, 1) == "?") then
		--zadanie pomocy!
		local temat = cmd:sub(2)
		local trigs = {}
		if(temat == "ogolne") then
			table.insert(trigs, tempRegexTrigger("([a-z]+)[ ]+-[ ]+([a-zA-Z]+.*)", [[
				makeHelpLink(matches[2], matches[3]);
			]]));
		end
		table.insert(trigs, tempRegexTrigger("(ZOBACZ TEZ|PATRZ TEZ|ZOBACZ TAKZE)", [[
			tempLineTrigger(1, 1, "makeHelpLinksFromSeeAlso(line)");
		]]));
		local s = ""
		for i, v in pairs(trigs) do
			s = s .. "killTrigger(" .. v .. ");\n"
		end
		s = s .. [[ killTrigger(trigKoniecPomocy); ]]
		trigKoniecPomocy = tempRegexTrigger("^(EOF|Nie ma pomocy na ten temat.)", s);
	end	
end

